con:config.script:
//1. ${#Project#название_переменной} — для проектных переменных.
//2. ${#TestSuite#название_переменной} — для переменных тест-сьюта.
//3. ${#TestCase#название_переменной} — для переменных тест-кейса.
//4  ${#Add#Response} - для получения переменной в ответе на запрос Add
Random rnd = new Random()
int a = (rnd.nextInt(100)) // random integer in the range of (?) 
int b = (rnd.nextInt(100))
testRunner.testCase.setPropertyValue( "intA", String.valueOf(a) ) //SetProperty требует приведение к String
testRunner.testCase.setPropertyValue( "intB", String.valueOf(b) ) // даже если переменная содержит int

//needs to save project before using projectFir
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
def projectDir = groovyUtils.projectPath

def response = context.expand( '${Divide#Response}' )
def file = new File(projectDir, "Divide" + "_response.txt" ).write( response )

